// AI-Powered Service Analysis
const items = $input.all();

// Extract data from different sources
const environmentData = items.find(item => item.json.environment)?.json || {};
const dockerData = items.find(item => item.json.Name && item.json.State);
const n8nData = items.find(item => item.json.data && Array.isArray(item.json.data));
const ollamaData = items.find(item => item.json.models);
const qdrantData = items.find(item => item.json.result);
const apiData = items.find(item => item.json.status === 'healthy');

// Analyze environment context
const environment = environmentData.environment || {};
const isDocker = environment.container_type === 'docker';
const hasGPU = environment.hardware?.gpu_available || false;
const totalRAM = environment.hardware?.memory_total_gb || 0;
const cpuCores = environment.hardware?.cpu_cores || 0;

// Service Status Analysis
const services = {
  docker_compose: {
    status: dockerData ? 'running' : 'stopped',
    containers: dockerData ? 1 : 0,
    health: dockerData?.State === 'running' ? 'healthy' : 'unhealthy'
  },
  n8n: {
    status: n8nData ? 'running' : 'stopped',
    workflows: n8nData?.data?.length || 0,
    health: n8nData ? 'healthy' : 'unhealthy'
  },
  ollama: {
    status: ollamaData ? 'running' : 'stopped',
    models: ollamaData?.models?.length || 0,
    health: ollamaData ? 'healthy' : 'unhealthy'
  },
  qdrant: {
    status: qdrantData ? 'running' : 'stopped',
    collections: qdrantData?.result?.collections?.length || 0,
    health: qdrantData ? 'healthy' : 'unhealthy'
  },
  api_server: {
    status: apiData ? 'running' : 'stopped',
    health: apiData ? 'healthy' : 'unhealthy'
  }
};

// AI Analysis and Recommendations
function generateAIInsights() {
  const insights = [];
  const warnings = [];
  const recommendations = [];

  // Performance Analysis
  if (totalRAM < 8) {
    warnings.push(`Low RAM detected (${totalRAM}GB). Consider upgrading for better performance.`);
    recommendations.push('Disable unnecessary services or upgrade to 16GB+ RAM');
  }

  if (!hasGPU && services.ollama.status === 'running') {
    insights.push('Ollama running on CPU. GPU acceleration would significantly improve performance.');
    recommendations.push('Consider NVIDIA GPU for AI workloads or use lighter models');
  }

  // Service Health Analysis
  const runningServices = Object.keys(services).filter(key => services[key].status === 'running');
  const healthyServices = Object.keys(services).filter(key => services[key].health === 'healthy');

  if (runningServices.length === Object.keys(services).length) {
    insights.push('ðŸŽ‰ All services are running! Your AI platform is fully operational.');
  } else {
    const stoppedServices = Object.keys(services).filter(key => services[key].status === 'stopped');
    warnings.push(`Services not running: ${stoppedServices.join(', ')}`);
    recommendations.push('Run: docker-compose up -d to start missing services');
  }

  // Model Analysis
  if (services.ollama.models === 0) {
    warnings.push('No Ollama models detected');
    recommendations.push('Pull models: docker exec ollama ollama pull llama2');
  } else {
    insights.push(`Ollama has ${services.ollama.models} model(s) available`);
  }

  // Workflow Analysis
  if (services.n8n.workflows === 0) {
    insights.push('No workflows detected in n8n');
    recommendations.push('Import demo workflows from n8n/demo-data/workflows/');
  } else {
    insights.push(`n8n has ${services.n8n.workflows} workflow(s) configured`);
  }

  // Vector Database Analysis
  if (services.qdrant.collections === 0) {
    insights.push('Qdrant vector database is empty');
    recommendations.push('Create collections for RAG applications');
  } else {
    insights.push(`Qdrant has ${services.qdrant.collections} collection(s)`);
  }

  return { insights, warnings, recommendations };
}

const aiAnalysis = generateAIInsights();

// Generate Performance Score
function calculatePerformanceScore() {
  let score = 0;
  let maxScore = 100;

  // Service availability (40 points)
  const runningServices = Object.keys(services).filter(key => services[key].status === 'running').length;
  const totalServices = Object.keys(services).length;
  score += (runningServices / totalServices) * 40;

  // Hardware score (30 points)
  if (hasGPU) score += 15;
  if (totalRAM >= 16) score += 10;
  else if (totalRAM >= 8) score += 5;
  if (cpuCores >= 8) score += 5;

  // Content score (30 points)
  if (services.ollama.models > 0) score += 10;
  if (services.n8n.workflows > 0) score += 10;
  if (services.qdrant.collections > 0) score += 10;

  return Math.round(score);
}

const performanceScore = calculatePerformanceScore();

// Generate Status Report
const statusReport = {
  timestamp: new Date().toISOString(),
  environment: {
    type: isDocker ? 'Docker Container' : 'Native',
    hardware: {
      cpu_cores: cpuCores,
      memory_gb: totalRAM,
      gpu_available: hasGPU
    }
  },
  services,
  performance: {
    score: performanceScore,
    grade: performanceScore >= 90 ? 'A' : performanceScore >= 80 ? 'B' : performanceScore >= 70 ? 'C' : 'D',
    status: performanceScore >= 80 ? 'Excellent' : performanceScore >= 60 ? 'Good' : 'Needs Improvement'
  },
  ai_analysis: aiAnalysis,
  summary: {
    total_services: Object.keys(services).length,
    running_services: Object.keys(services).filter(key => services[key].status === 'running').length,
    healthy_services: Object.keys(services).filter(key => services[key].health === 'healthy').length,
    total_models: services.ollama.models,
    total_workflows: services.n8n.workflows,
    total_collections: services.qdrant.collections
  }
};

return [{ json: statusReport }];

// Generate Human-Readable Status Report
